[System.Windows.Forms.MessageBox]::Show($Message1,"Message",0,64)

}

catch {

$Error1 = $Error[0].Exception.Message
[System.Windows.Forms.MessageBox]::Show($Error1,"Message",0,16)

Remove-ADUser $Login -Confirm:$false

}
}
}
